import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import io from 'socket.io-client';

const SOCKET_SERVER = 'http://localhost:3001';

export default function Room() {
  const { roomId } = useParams();
  const navigate = useNavigate();
  const [socket, setSocket] = useState(null);
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    // Connect to socket server
    const socketInstance = io(SOCKET_SERVER);
    setSocket(socketInstance);

    // Join room when component mounts
    socketInstance.emit('join_room', roomId);

    // Listen for messages
    socketInstance.on('receive_message', (data) => {
      setMessages((prev) => [...prev, data]);
    });

    // Listen for user joined notifications
    socketInstance.on('user_joined', (data) => {
      setMessages((prev) => [...prev, { 
        system: true, 
        message: data.message 
      }]);
    });

    // Listen for user left notifications
    socketInstance.on('user_left', (data) => {
      setMessages((prev) => [...prev, { 
        system: true, 
        message: data.message 
      }]);
    });

    // Cleanup on unmount
    return () => {
      socketInstance.emit('leave_room', roomId);
      socketInstance.disconnect();
    };
  }, [roomId]);

  const sendMessage = (e) => {
    e.preventDefault();
    if (message.trim() && socket) {
      socket.emit('send_message', {
        room: roomId,
        message: message.trim()
      });
      setMessage('');
    }
  };

  const leaveRoom = () => {
    if (socket) {
      socket.emit('leave_room', roomId);
      navigate('/');
    }
  };

  return (
    <div className="container mt-4">
      <div className="card">
        <div className="card-header d-flex justify-content-between align-items-center">
          <h3>Room: {roomId}</h3>
          <button 
            className="btn btn-danger" 
            onClick={leaveRoom}
          >
            Leave Room
          </button>
        </div>
        <div className="card-body">
          <div 
            className="messages-container" 
            style={{ 
              height: '400px', 
              overflowY: 'auto',
              marginBottom: '20px'
            }}
          >
            {messages.map((msg, idx) => (
              <div 
                key={idx} 
                className={`message ${msg.system ? 'text-muted' : ''}`}
                style={{ marginBottom: '10px' }}
              >
                {msg.system ? (
                  <em>{msg.message}</em>
                ) : (
                  <>
                    <strong>{msg.userId.slice(0, 6)}: </strong>
                    {msg.message}
                    <small className="text-muted ms-2">
                      {new Date(msg.timestamp).toLocaleTimeString()}
                    </small>
                  </>
                )}
              </div>
            ))}
          </div>
          <form onSubmit={sendMessage} className="input-group">
            <input
              type="text"
              className="form-control"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              placeholder="Type a message..."
            />
            <button 
              type="submit" 
              className="btn btn-primary"
            >
              Send
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
